async function createConfig () {
  const katex = (await import('rehype-katex')).default;
  config.presets[0][1].blog.remarkPlugins.push(math);
  config.presets[0][1].blog.rehypePlugins.push(katex);
  return config;
};
module.exports = createConfig;
